// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract MockToken is ERC20 {
    bool public failTransferFrom = false;

    constructor(string memory name, string memory symbol) ERC20(name, symbol) {}

    function mint(address to, uint256 amount) external {
        _mint(to, amount);
    }

    function setFailTransferFrom(bool _value) external {
        failTransferFrom = _value;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        if (failTransferFrom) {
            return false;
        }

        // Call the original transferFrom function
        return super.transferFrom(sender, recipient, amount);
    }
}

contracts/vesting/interfaces/IVesting.so.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

import {IAtomicSwapBase} from "../../abstracts/interfaces/IAtomicSwapBase.sol";

/// @title IVesting
/// @notice Interface for the Vesting contract.
/// @dev Provides function signatures and structures for vesting operations.
interface IVesting {
    /// @notice Represents a vesting schedule for a beneficiary.
    /// @param from The address that created the vesting schedule.
    /// @param start The start time of the vesting schedule.
    /// @param token The token address being vested.
    /// @param totalAmount The total amount of tokens to be vested.
    /// @param amountReleased The amount of tokens already released.
    /// @param lastReleasedStep The last step index that was processed for release.
    struct VestingSchedule {
        address from;
        uint256 start;
        uint256 lastReleasedTime;
        address token;
        uint256 totalAmount;
        uint256 amountReleased;
        uint256 lastReleasedStep;
    }

    struct VestingInfo {
        VestingSchedule schedule;
        IAtomicSwapBase.Release[] release;
        address beneficiary; 
        bytes32 orderId;
    }

    /// @notice Starts the vesting schedule for a beneficiary.
    /// @param beneficiary The address of the beneficiary.
    /// @param token The token to be vested.
    /// @param totalAmount The total amount of tokens to be vested.
    /// @param releases The release schedule parameters.
    function startVesting(
        bytes32 orderId, 
        address beneficiary,
        address token,
        uint256 totalAmount,
        IAtomicSwapBase.Release[] memory releases
    ) external payable;

    event NewVesting(VestingInfo indexed vesting);
    /// @notice Event emitted when tokens are released to a beneficiary.
    /// @param beneficiary The address of the beneficiary.
    /// @param amount The amount of tokens released.
    event Released(address indexed beneficiary, uint256 indexed amount);

    /// @notice Event emitted when Ether is received.
    /// @param sender The address of the sender.
    /// @param amount The amount of Ether received.
    event Received(address indexed sender, uint256 indexed amount);

    // Custom errors for vesting operation failures.
    error VestingAlreadyStarted(address beneficiary);
    error VestingNotStarted();
    error InvalidVesting();
    error NoVestedTokensAvailable();
    error OverMaximumReleaseStep();
    error InvalidTotalPercentage();
    error NoVestedTokensForRelease();
}

contracts/vesting/Vesting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import {OwnablePausableUpgradeable} from "../abstracts/OwnablePausableUpgradeable.sol";
import {ReentrancyGuardUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";
import {TransferHelper} from "@uniswap/lib/contracts/libraries/TransferHelper.sol";
import {IVesting, IAtomicSwapBase} from "./interfaces/IVesting.sol";
import {AtomicSwapMsgValidator} from "../abstracts/libs/utils/AtomicSwapMsgValidator.sol";
/// @title Vesting Contract
/// @notice Implements vesting schedules for token distribution with a cliff period.
/// @dev Utilizes OpenZeppelin's Ownable and ReentrancyGuard contracts for security.
contract Vesting is OwnablePausableUpgradeable, ReentrancyGuardUpgradeable, IVesting {
    using AtomicSwapMsgValidator for *;
    /// @notice Stores vesting schedules for each beneficiary.
    mapping(address => mapping(bytes32 => VestingSchedule))
        public vestingSchedules;

    /// @notice Stores release information for each beneficiary.
    // slither-disable-next-line uninitialized-state
    mapping(address => mapping(bytes32 => IAtomicSwapBase.Release[]))
        public releaseInfos;

    /// @notice Initializes the vesting contract with necessary parameters.
    /// @param _admin The address of the admin.
    function initialize(address _admin) external initializer {
        __OwnablePausableUpgradeable_init(_admin);
    }
    // TODO: Check this function, anyone can call startVesting directly without takeSwap operation
    /// @notice Starts the vesting schedule for a beneficiary.
    /// @param beneficiary The address of the beneficiary.
    /// @param token The token address for vesting.
    /// @param totalAmount The total amount of tokens to be vested.
    /// @param releases Array of release schedules.
    /// @dev Sets up the vesting schedule and release information for the beneficiary.
    function startVesting(
        bytes32 orderId,
        address beneficiary,
        address token,
        uint256 totalAmount,
        IAtomicSwapBase.Release[] memory releases
    ) external payable nonReentrant onlyAdmin {
        // Ensure the uniqueness of 'beneficiary-orderId', to avoid an override call attack.
        if (vestingSchedules[beneficiary][orderId].from != address(0)) { 
            revert IAtomicSwapBase.DuplicateReleaseSchedule();
        }
        releases.validateVestingParams();

        if (token == address(0)) {
            if (msg.value != totalAmount) {
                revert IAtomicSwapBase.NotEnoughFund(totalAmount, msg.value);
            }
        } else {
            TransferHelper.safeTransferFrom(
                token,
                msg.sender, address(this), totalAmount
            );
        }
        uint256 vestingStartTime = block.timestamp;
        VestingSchedule memory newVesting = VestingSchedule({
            from: msg.sender,
            start: vestingStartTime,
            lastReleasedTime: vestingStartTime,
            token: token,
            totalAmount: totalAmount,
            amountReleased: 0,
            lastReleasedStep: 0
        });
        vestingSchedules[beneficiary][orderId] = newVesting;
        //slither-disable-next-line uninitialized-state-variables
        IAtomicSwapBase.Release[] storage _releases = releaseInfos[beneficiary][
            orderId
        ];
        for (uint256 i = 0; i < releases.length; i++) {
            _releases.push(releases[i]);
        }
        emit NewVesting(
            VestingInfo(newVesting, releases, beneficiary, orderId)
        );
    }

    /// @notice Releases vested tokens to the beneficiary.
    /// @param beneficiary The address of the beneficiary to release tokens to.
    /// @dev Calculates the amount of tokens to be released and transfers them to the beneficiary.
    function release(
        address beneficiary,
        bytes32 orderId
    ) external nonReentrant whenNotPaused {
        VestingSchedule storage schedule = vestingSchedules[beneficiary][
            orderId
        ];
        IAtomicSwapBase.Release[] storage releases = releaseInfos[beneficiary][
            orderId
        ];
        if (
            releases.length == 0 || schedule.lastReleasedStep >= releases.length
        ) {
            revert InvalidVesting();
        }
        uint256 amountForRelease = 0;
        uint256 releaseTime = schedule.start;
        for (uint256 i = 0; i < releases.length; i++) {
            releaseTime += releases[i].durationInHours * 1 hours;
            if (i < schedule.lastReleasedStep) continue; // continue to next
            if (block.timestamp < releaseTime) {
                break;
            }
            uint256 releaseAmount = (schedule.totalAmount *
            releases[i].percentage) / 10000;
            amountForRelease += releaseAmount;
            schedule.lastReleasedStep = i + 1;
        }

        if (amountForRelease <= 0) {
            revert NoVestedTokensForRelease();
        }
        schedule.amountReleased += amountForRelease;
        if (schedule.token != address(0)) {
            TransferHelper.safeTransfer(
                schedule.token,
                beneficiary, amountForRelease
            );
        } else {
            TransferHelper.safeTransferETH(beneficiary, amountForRelease);
        }
        emit Released(beneficiary, amountForRelease);
    }

    /// @notice Fallback function to receive Ether.
    /// @dev Emits a Received event when Ether is received.
    receive() external payable onlyAdmin {
        emit Received(msg.sender, msg.value);
    }
}

